mapper.select:
SELECT
,yayi.yayi_uuid_number as yayiUuidNumber ,yayi.yayi_name as yayiName ,yayi.yayi_phone as yayiPhone ,yayi.yayi_id_number as yayiIdNumber ,yayi.yayi_photo as yayiPhoto ,yayi.yayi_email as yayiEmail ,yayi.new_money as newMoney ,yayi.zhiwei_types as zhiweiTypes ,yayi.yayi_content as yayiContent ,yayi.yayi_shanchang_content as yayiShanchangContent ,yayi.yayi_rongyu_content as yayiRongyuContent ,yayi.jinyong_types as jinyongTypes ,yonghu.yonghu_uuid_number as yonghuUuidNumber ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_email as yonghuEmail ,yonghu.new_money as newMoney ,yonghu.jinyong_types as jinyongTypes
FROM yayi_collection a left JOIN yayi yayi ON a.yayi_id = yayi.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and ( a.yayi_id = #{params.yayiId} )
and ( a.yonghu_id = #{params.yonghuId} )
and a.yayi_collection_types = #{params.yayiCollectionTypes}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.insert_time = #{params.insertTime}
and a.yayi_id IS NOT NULL
and yayi.yayi_uuid_number like CONCAT('%',#{params.yayiUuidNumber},'%')
and yayi.yayi_name like CONCAT('%',#{params.yayiName},'%')
and yayi.yayi_phone like CONCAT('%',#{params.yayiPhone},'%')
and yayi.yayi_id_number like CONCAT('%',#{params.yayiIdNumber},'%')
and yayi.yayi_email like CONCAT('%',#{params.yayiEmail},'%')
and yayi.new_money >= #{params.newMoneyStart}
and yayi.new_money <= #{params.newMoneyEnd}
and a.new_money = #{params.newMoney}
and yayi.zhiwei_types = #{params.zhiweiTypes}
and yayi.yayi_content like CONCAT('%',#{params.yayiContent},'%')
and yayi.yayi_shanchang_content like CONCAT('%',#{params.yayiShanchangContent},'%')
and yayi.yayi_rongyu_content like CONCAT('%',#{params.yayiRongyuContent},'%')
and yayi.jinyong_types = #{params.jinyongTypes}
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_uuid_number like CONCAT('%',#{params.yonghuUuidNumber},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.new_money >= #{params.newMoneyStart}
and yonghu.new_money <= #{params.newMoneyEnd}
and a.new_money = #{params.newMoney}
and yonghu.jinyong_types = #{params.jinyongTypes}
order by a.${params.sort} ${params.order}
